const t = document.querySelector("#comment"), e = document.querySelector("#dayhour"), n = document.querySelector("#humidity"), o = document.querySelector("#temp"), r = document.querySelector("#wind"); fetch("https://weatherdbi.herokuapp.com/data/weather/innopolis").then((t => t.json())).then((c => { t.textContent = c.currentConditions.comment, e.textContent = c.currentConditions.dayhour, n.textContent = c.currentConditions.humidity, o.textContent = c.currentConditions.temp.c, r.textContent = c.currentConditions.wind.km }));
